import React, { useState } from "react";
import { Route, Redirect } from "react-router-dom";
import GoogleLogin from "react-google-login";
import Main from "./Main";

function Authenticate(props) {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isFirstTime, setIsFirstTime] = useState(true);
  const [userDetails, setUserDetails] = useState({
    "firstName": "",
    "lastName": "",
    "email": "",
    "imageURL": "",
    "isAuthenticated": false,
  });

  function loginSuccess(response) {
    console.log(response.profileObj.name);
    setIsLoggedIn(true);
    setIsFirstTime(false);
    // console.log(response.profileObj.givenName);
    setUserDetails({
      "firstName": response.profileObj.givenName,
      "lastName": response.profileObj.lastName,
      "email": response.profileObj.email,
      "imageURL": response.profileObj.imageUrl,
      "isAuthenticated": true,
    });
    console.log(userDetails);
  }
  function loginFailure(response) {
    setIsLoggedIn(false);
    setIsFirstTime(false);
  }
  return (
    <div>
      <h1>{props.action} using Google.</h1>
      <GoogleLogin
        clientId="49371901320-8a2u0ldp967umqi44phevv8uts1br3rq.apps.googleusercontent.com"
        buttonText={props.action}
        onSuccess={(response) => loginSuccess(response)}
        onFailure={(response) => loginFailure(response)}
        cookiePolicy={"single_host_origin"}
      />
      {isLoggedIn ? (
        <Redirect to={{ pathname: "/main", state: { ...userDetails } }} />
      ) : isFirstTime ? null : (
        "Authorization failed, please try again."
      )}
    </div>
  );
}

export default Authenticate;
