import React, { useState, useEffect } from "react";
import { Route, Redirect } from "react-router-dom";
import GoogleLogin from "react-google-login";
import Main from "./Main";

function Authenticate(props) {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isFirstTime, setIsFirstTime] = useState(true);
  const [userDetails, setUserDetails] = useState({});
  // useEffect(() => {
  //   setIsLoggedIn(true);
  // }, [isLoggedIn]);
  // useEffect(() => {
  //   setIsFirstTime(false);
  // }, [isFirstTime]);
  return (
    <div>
      <h1>{props.action} using Google.</h1>
      <GoogleLogin
        clientId="49371901320-8a2u0ldp967umqi44phevv8uts1br3rq.apps.googleusercontent.com"
        buttonText={props.action}
        onSuccess={(response) => {
          setIsLoggedIn(true);
          console.log(response.profileObj);
          setIsFirstTime(false);
          setUserDetails({firstName: response.profileObj.name});
          console.log("isFirstTime is " + isFirstTime)
          console.log("is Logged in is in onSuccess " + isLoggedIn)
          console.log("userDetails " + userDetails);
        }}
        onFailure={(response) => console.log("there is an error " + response)}
        cookiePolicy={"single_host_origin"}
      />
      {console.log("is Logged in is in div " + isLoggedIn)}
      {isLoggedIn ? (
        <p>Logged in</p>
      ) : isFirstTime ? null : (
        "Authorization failed, please try again."
      )}
    </div>
  );
}

export default Authenticate;
