import React, { useState } from "react";

function Assignment() {
    let description = "Rational Numbers assignment, very important for your next exam. A rational number is a number that is expressed as the ratio of two integers, where the denominator should not be equal to zero, whereas an irrational number cannot be expressed in the form of fractions."
    const [readMore, setReadMore] = useState(false);
  return (
    <div>
      <h5 className="title-left">Assignment 1</h5>
      <p className="title-right">
        Submitted: <span className="marks">15/20</span>
      </p>
      <p style={{color: " #40c8e0"}}>Maths</p>
      <p>{readMore ? description : description.slice(0, 47) + "..."}</p>
      <p className="read-more"></p>
    </div>
  );
}
export default Assignment;
